INSERT INTO `greek_lexicon` (`number`, `orig_word`, `word_orig`, `translit`, `tdnt`, `phonetic`, `part_of_speech`, `st_def`, `IPD_def`) VALUES
(1, 'A', 'of Hebrew origin', 'a', '1:1,*', 'al\'-fah', 'Letter', 'of Hebrew origin; the first letter of the alphabet; figuratively, only (from its use as a numeral) the first: --Alpha. Often used (usually an, before a vowel) also in composition (as a contraction from «427») in the sense of privation; so, in many words, beginning with this letter; occasionally in the sense of union (as a contraction of «260»).', '<OL TYPE=\"1\"><LI>first letter of Greek alphabet<LI>Christ is the Alpha to indicate that he is the beginning and the end</OL>'),
(2, 'ÅAarwvn', 'of Hebrew origin (0175)', 'Aaron', '1:3,1', 'ah-ar-ohn\'', 'Proper Name Masculine', 'of Hebrew origin («0175»); Aaron, the brother of Moses:--Aaron.', 'Aaron = \"light-bringer\"<OL TYPE=\"1\"><LI>the brother of Moses, the first high priest of Israel and head of the whole priestly order</OL>'),
(3, 'ÅAbaddwvn', 'of Hebrew origin (011)', 'Abaddon', '1:4,1', 'ab-ad-dohn\'', 'Proper Name Masculine', 'of Hebrew origin («011»); a destroying angel:--Abaddon.', 'Abaddon = \"destruction\"<OL TYPE=\"1\"><LI>ruin<LI>destruction<LI>the place of destruction<LI>the name of the angel-prince of the infernal regions, the minister of death and the author of havoc on the earth</OL>'),
(4, 'ajbarhvß', 'from (1) (as a negative particle) and (922)', 'abares', 'None', 'ab-ar-ace\'', 'Adjective', 'from «1» (as a negative particle) and «922»; weightless, i.e. (figuratively) not burdensome:--from being burdensome.', '<OL TYPE=\"1\"><LI>not burdensome<LI>not heavy<LI>light without weight</OL>'),
(5, 'ÅAbbav', 'of Aramaic origin (02)', 'Abba', '1:5,1', 'ab-bah\'', 'Noun', 'of Chaldee origin («02»); father as a vocative:--Abba.', 'Abba = \"father\"<OL TYPE=\"1\"><LI>father, customary title used of God in prayer. Whenever it occurs in the New Testament it has the Greek interpretation joined to it, that is apparently to be explained by the fact that the Chaldee \"ABBA\" through frequent use in prayer, gradually acquired the nature of a most sacred proper name, to which the Greek speaking Jews added the name from their own tongue.</OL>'),
(6, 'áAbel', 'of Hebrew origin (01893)', 'Abel', '*,2', 'ab\'-el', 'Proper Name Masculine', 'of Hebrew origin («01893»); Abel, the son of Adam:--Abel.', 'Abel = \"vanity (that is: transitory)\"<OL TYPE=\"1\"><LI>the second son of Adam, murdered by his brother Cain</OL>'),
(7, 'ÅAbiav', 'of Hebrew origin (029)', 'Abia', 'None', 'ab-ee-ah\'', 'Proper Name Masculine', 'of Hebrew origin («029»); Abijah, the name of two Israelites:--Abia.', 'Abijah = \"my father is Jah (Jehovah)\"<OL TYPE=\"1\"><LI>a priest, the head of a priestly family from whom when David divided the priests into 24 classes, Abia was the 8th order<LI>son and successor to Rehoboam on the throne of Judah</OL>'),
(8, 'ÅAbiaqavr', 'of Hebrew origin (054)', 'Abiathar', 'None', 'ab-ee-ath\'-ar', 'Proper Name Masculine', 'of Hebrew origin («054»); Abiathar, an Israelite:--Abiathar.', 'Abiathar = \"father of abundance\"<OL TYPE=\"1\"><LI>the name of a high priest</OL>'),
(9, 'ÅAbilhnhv', 'of foreign origin, cf (58)', 'Abilene', 'None', 'ab-ee-lay-nay\'', 'Proper Name Location', 'of foreign origin (compare «058»); Abilene, a region of Syria:--Abilene.', 'Abilene = \"grassy meadow\"<OL TYPE=\"1\"><LI>a region of Syria between Lebanon and Hermon towards Phoenicia, 18 miles (29 km) from Damascus and 37 miles (60 km) from Heliopolis</OL>'),
(10, 'ÅAbiouvd', 'of Hebrew origin (031)', 'Abioud', 'None', 'ab-ee-ood\'', 'Proper Name Location', 'of Hebrew origin («031»); Abihud, an Israelite:--Abiud.', 'Abiud = \"my father is majesty\"<OL TYPE=\"1\"><LI>the son of Bela and grandson of Benjamin (1 Ch. 8:</OL>'),
(11, 'ÅAbraavm', 'of Hebrew origin (085)', 'Abraam', '1:8,2', 'ab-rah-am\'', 'Proper Name Masculine', 'of Hebrew origin («085»); Abraham, the Hebrew patriarch:--Abraham. (In Acts 7:16 the text should probably read Jacob.)', 'Abraham = \"father of a multitude\"<OL TYPE=\"1\"><LI>the son of Terah and the founder of the Jewish nation</OL>'),
(12, 'a[bussoß', 'from (1) (as a negative particle) and a variation of (1037)', 'abussos', '1:9,2', 'ab\'-us-sos', 'Noun Feminine', 'from «1» (as a negative particle) and a variation of «1037»; depthless, i.e. (specially) (infernal) \"abyss\":--deep, (bottomless) pit.', '<OL TYPE=\"1\"><LI>bottomless<LI>unbounded<LI>the abyss<OL TYPE=\"a\"><LI>the pit<LI>the immeasurable depth<LI>of Orcus, a very deep gulf or chasm in the lowest parts of the earth used as the common receptacle of the dead and especially as the abode of demons</OL></OL>'),
(13, 'àAgaboß', 'of Hebrew origin, cf (02285)', 'Agabos', 'None', 'ag\'-ab-os', 'Proper Name Masculine', 'of Hebrew origin (compare «02285»); Agabus, an Israelite:--Agabus.', 'Agabus = \"locust\"<OL TYPE=\"1\"><LI>a Christian prophet</OL>'),
(14, 'ajgaqoergevw', 'from (18) and (2041)', 'agathoergeo', '1:17,3', 'ag-ath-er-gheh\'-o', 'Verb', 'from «18» and «2041»; to work good:--do good.', '<OL TYPE=\"1\"><LI>to work good, to do good, to do well, act rightly</OL>'),
(15, 'ajgaqopoievw', 'from (17)', 'agathopoieo', '1:17,3', 'ag-ath-op-oy-eh\'-o', 'Verb', 'from «17»; to be a well-doer (as a favor or a duty):--(when) do good (well).', '<OL TYPE=\"1\"><LI>to do good, do something which profits others<OL TYPE=\"a\"><LI>to be a good help to someone<LI>to do someone a favour<LI>to benefit</OL><LI>to do well, do right</OL>'),
(16, 'ajgaqopoii?a', 'from (17)', 'agathopolia', '1:17,3', 'ag-ath-op-oy-ee\'-ah', 'Noun Feminine', 'from «17»; well-doing, i.e. virtue:--well-doing.', '<OL TYPE=\"1\"><LI>a course of right action, well doing, virtue</OL>'),
(17, 'ajgaqopoiovß', 'from (18) and (4160)', 'agathopoios', '1:17,3', 'ag-ath-op-oy-os\'', 'Adjective', 'from «18» and «4160»; a well-doer, i.e. virtuous:--them that do well.', '<OL TYPE=\"1\"><LI>acting rightly, doing well, virtuous</OL>'),
(18, 'ajgaqovß', 'a primary word', 'agathos', '1:10,3', 'ag-ath-os\'', 'Adjective', 'a primary word; \"good\" (in any sense, often as noun):--benefit, good(-s, things), well. Compare «2570».', '<OL TYPE=\"1\"><LI>of good constitution or nature<LI>useful, salutary<LI>good, pleasant, agreeable, joyful, happy<LI>excellent, distinguished<LI>upright, honourable</OL>'),
(19, 'ajgaqourgevw', 'from (18)', 'agathosune', '1:18,3', 'ag-ath-o-soo\'-nay', 'Noun Feminine', 'from «18»; goodness, i.e. virtue or beneficence:--goodness.', '<OL TYPE=\"1\"><LI>uprightness of heart and life, goodness, kindness</OL>'),
(20, 'ajgallivasiß', 'from (21)', 'agalliasis', '1:19,4', 'ag-al-lee\'-as-is', 'Noun Feminine', 'from «21»; exultation; specially, welcome:--gladness, (exceeding) joy.', '<OL TYPE=\"1\"><LI>exultation, extreme joy, gladness</OL>'),
(21, 'ajgalliavw', 'from agan (much) and (242)', 'agalliao', '1:19, *', 'ag-al-lee-ah\'-o', 'Verb', 'from agan (much) and «242»; properly, to jump for joy, i.e. exult:--be (exceeding) glad, with exceeding joy, rejoice (greatly).', '<OL TYPE=\"1\"><LI>to exult, rejoice exceedingly, be exceeding glad</OL>'),
(22, 'a[gamoß', 'from (1) (as a negative particle) and (1062)', 'agamos', 'None', 'ag\'-am-os', 'Adjective', 'from «1» (as a negative particle) and «1062»; unmarried:--unmarried.', '<OL TYPE=\"1\"><LI>unmarried, unwedded, single</OL>'),
(23, 'ajganaktevw', 'from agan (much) and achthos (grief, akin to the base of (43))', 'aganakteo', 'None', 'ag-an-ak-teh\'-o', 'Verb', 'from agan (much) and achthos (grief; akin to the base of «43»); to be greatly afflicted, i.e. (figuratively) indignant:--be much (sore) displeased, have (be moved with, with) indignation.', '<OL TYPE=\"1\"><LI>to be indignant, moved with indignation, be very displeased</OL>'),
(24, 'ajganavkthsiß', 'from (23)', 'aganaktesis', 'None', 'ag-an-ak\'-tay-sis', 'Noun Feminine', 'from «23»; indignation:--indignation.', '<OL TYPE=\"1\"><LI>indignation, irritation, vexation</OL>'),
(25, 'ajgapavw', 'perhaps from agan (much) [or cf (5368)]', 'agapao', '1:21,5', 'ag-ap-ah\'-o', 'Verb', 'perhaps from agan (much) (or compare «5689»); to love (in a social or moral sense):--(be-)love(-ed). Compare «5368».', '<OL TYPE=\"1\"><LI>of persons<OL TYPE=\"a\"><LI>to welcome, to entertain, to be fond of, to love dearly</OL><LI>of things<OL TYPE=\"a\"><LI>to be well pleased, to be contented at or with a thing</OL></OL>'),
(26, 'ajgavph', 'from (25)', 'agape', '1:21,5', 'ag-ah\'-pay', 'Noun Feminine', 'from «25»; love, i.e. affection or benevolence; specially (plural) a love-feast:--(feast of) charity(-ably), dear, love.', '<OL TYPE=\"1\"><LI>brotherly love, affection, good will, love, benevolence<LI>love feasts</OL>'),
(27, 'ajgaphtovß', 'from (25)', 'agapetos', '1:21,5', 'ag-ap-ay-tos\'', 'Adjective', 'from «25»; beloved:--(dearly, well) beloved, dear.', '<OL TYPE=\"1\"><LI>beloved, esteemed, dear, favourite, worthy of love</OL>'),
(28, 'ÔAgavr', 'of Hebrew origin (01904)', 'Agar', '1:55,10', 'ag\'-ar', 'Proper Name Feminine', 'of Hebrew origin («01904»); Hagar, the concubine of Abraham:--Hagar.', 'Hagar = \"flight\"<OL TYPE=\"1\"><LI>Abraham\'s concubine and mother to Ishmael</OL>'),
(29, 'ajggareuvw', 'of foreign origin, cf (0104)', 'aggareuo', 'None', 'ang-ar-yew\'-o', 'Verb', 'of foreign origin (compare «0104»); properly, to be a courier, i.e. (by implication) to press into public service:--compel (to go).', '<OL TYPE=\"1\"><LI>to employ a courier, dispatch a mounted messenger, press into public service, compel to go</OL>'),
(30, 'ajggei\'on', 'from aggos (a pail, perhaps as bent, cf base of (43))', 'aggeion', 'None', 'ang-eye\'-on', 'Noun Neuter', 'from aggos (a pail, perhaps as bent; compare the base of «43»); a receptacle:--vessel.', '<OL TYPE=\"1\"><LI>a vessel, receptacle, a pail, a reservoir</OL>'),
(31, 'ajggeliva', 'from (32)', 'aggelia', '1:56,10', 'ang-el-ee\'-ah', 'Noun Feminine', 'from «32»; an announcement, i.e. (by implication) precept:--message.', '<OL TYPE=\"1\"><LI>message, announcement, news<LI>a proclamation, command, order</OL>'),
(32, 'a[ggeloß', 'from aggello [probably derived from (71), cf (34)] (to bring tidings)', 'aggelos', '1:74,12', 'ang\'-el-os', 'Noun Masculine', 'from aggello (probably derived from «71»; compare «34») (to bring tidings); a messenger; especially an \"angel\"; by implication, a pastor:--angel, messenger.', '<OL TYPE=\"1\"><LI>a messenger, envoy, one who is sent, an angel, a messenger from God</OL>'),
(33, 'a[ge', 'imperative of (71)', 'age', 'None', 'ag\'-eh', '<OL TYPE=\"1\"><LI>go to, come!, come now!</OL>', 'imperative of «71»; properly, lead, i.e. come on:--go to.', ''),
(34, 'ajgevlh', 'from (71) [cf (32)]', 'agele', 'None', 'ag-el\'-ay', 'Noun Feminine', 'from «71» (compare «32»); a drove:--herd.', '<OL TYPE=\"1\"><LI>herd or oxen or cattle, a herd or company</OL>'),
(35, 'ajgenealovghtoß', 'from (1) (as negative particle) and (1075)', 'agenealogetos', '1:665,114', 'ag-en-eh-al-og\'-ay-tos', 'Adjective', 'from «1» (as negative particle) and «1075»; unregistered as to birth:--without descent.', '<OL TYPE=\"1\"><LI>one whose descent there is no record of, without genealogy</OL>'),
(36, 'ajgenhvß', 'from (1) (as negative particle) and (1085)', 'agenes', 'None', 'ag-en-ace\'', 'Adjective', 'from «1» (as negative particle) and «1085»; properly, without kin, i.e. (of unknown descent, and by implication) ignoble:--base things.', '<OL TYPE=\"1\"><LI>ignoble, coward, mean, base, of no family, that is: low born</OL>'),
(37, 'aJgiavzw', 'from (40)', 'hagiazo', '1:111,14', 'hag-ee-ad\'-zo', 'Verb', 'from «40»; to make holy, i.e. (ceremonially) purify or consecrate; (mentally) to venerate:--hallow, be holy, sanctify.', '<OL TYPE=\"1\"><LI>to render or acknowledge, or to be venerable or hallow<LI>to separate from profane things and dedicate to God<OL TYPE=\"a\"><LI>consecrate things to God<LI>dedicate people to God</OL><LI>to purify<OL TYPE=\"a\"><LI>to cleanse externally<LI>to purify by expiation: free from the guilt of sin<LI>to purify internally by renewing of the soul</OL></OL>'),
(38, 'aJgiasmovß', 'from (37)', 'hagiasmos', '1:113,14', 'hag-ee-as-mos\'', 'Noun Masculine', 'from «37»; properly, purification, i.e. (the state) purity; concretely (by Hebraism) a purifier:--holiness, sanctification.', '<OL TYPE=\"1\"><LI>consecration, purification<LI>the effect of consecration<OL TYPE=\"a\"><LI>sanctification of heart and life</OL></OL>'),
(39, 'a&gion', 'neuter of (40)', 'hagion', 'None', 'hag\'-ee-on', 'Adjective', 'neuter of «40»; a sacred thing (i.e. spot):--holiest (of all), holy place, sanctuary.', '<OL TYPE=\"1\"><LI>reverend, worthy of veneration<OL TYPE=\"a\"><LI>of things which on account of some connection with God possess a certain distinction and claim to reverence, as places sacred to God which are not to be profaned<LI>of persons whose services God employs, for example, apostles</OL><LI>set apart for God, to be as it were, exclusively his<LI>services and offerings<OL TYPE=\"a\"><LI>prepared for God with solemn rite, pure, clean</OL><LI>in a moral sense, pure sinless upright holy</OL>'),
(40, 'a&gioß', 'from hagos (an awful thing) [cf (53), (2282)]', 'hagios', '1:88,14', 'hag\'-ee-os', 'Adjective', 'from hagos (an awful thing) (compare «53», «2282»); sacred (physically, pure, morally blameless or religious, ceremonially, consecrated):--(most) holy (one, thing), saint.', '<OL TYPE=\"1\"><LI>most holy thing, a saint</OL>'),
(41, 'aJgiovthß', 'from (40)', 'hagiotes', '1:114,14', 'hag-ee-ot\'-ace', 'Noun Feminine', 'from «40»; sanctity (i.e. properly, the state):--holiness.', '<OL TYPE=\"1\"><LI>sanctity<LI>in a moral sense: holiness</OL>'),
(42, 'aJgiwsuvnh', 'from (40)', 'hagiosune', '1:114,14', 'hag-ee-o-soo\'-nay', 'Noun Feminine', 'from «40»; sacredness (i.e. properly, the quality):--holiness.', '<OL TYPE=\"1\"><LI>majesty, holiness<LI>moral purity</OL>'),
(43, 'ajgkavlh', 'from agkos (a bend, \"ache\")', 'agkale', 'None', 'ang-kal\'-ay', 'Noun Feminine', 'from agkos (a bend, \"ache\"); an arm (as curved):--arm.', '<OL TYPE=\"1\"><LI>the curve or inner angle of the arm, the bent arm<LI>anything closely enfolding, as the arms of the sea, etc.</OL>'),
(44, 'a[gkistron', 'from the same as (43)', 'agkistron', 'None', 'ang\'-kis-tron', 'Noun Neuter', 'from the same as «43»; a hook (as bent):--hook.', '<OL TYPE=\"1\"><LI>a fishing hook, a small hook</OL>'),
(45, 'a[gkura', 'from the same as (43)', 'agkura', 'None', 'ang\'-koo-rah', 'Noun Feminine', 'from the same as «43»; an \"anchor\" (as crooked):--anchor.', '<OL TYPE=\"1\"><LI>an anchor<LI>metaph. any stay or safeguard</OL>'),
(46, 'a[gnafoß', 'from (1) (as a negative particle) and the same as (1102)', 'agnaphos', 'None', 'ag\'-naf-os', 'Adjective', 'from «1» (as a negative particle) and the same as «1102»; properly, unfulled, i.e. (by implication) new (cloth):--new.', '<OL TYPE=\"1\"><LI>unmilled, unfulled, undressed, unprocessed, new</OL>'),
(47, 'aJgneiva', 'from (53)', 'hagneia', '1:123,19', 'hag-ni\'-ah', 'Noun Feminine', 'from «53»; cleanliness (the quality), i.e. (specially) chastity:--purity.', '<OL TYPE=\"1\"><LI>purity, sinlessness of life</OL>'),
(48, 'aJgnivzw', 'from (53)', 'hagnizo', '1:123,19', 'hag-nid\'-zo', 'Verb', 'from «53»; to make clean, i.e. (figuratively) sanctify (ceremonially or morally):--purify (self).', '<OL TYPE=\"1\"><LI>ceremonially<OL TYPE=\"a\"><LI>to make pure, purify, cleanse</OL><LI>morally</OL>'),
(49, 'aJgnismovß', 'from (48)', 'hagnismos', '1:124,19', 'hag-nis-mos\'', 'Noun Masculine', 'from «48»; a cleansing (the act), i.e. (ceremonially) lustration:--purification.', '<OL TYPE=\"1\"><LI>purification, expiation</OL>'),
(50, 'ajgnoevw', 'from (1) (as a negative particle) and (3539)', 'agnoeo', '1:115,18', 'ag-no-eh\'-o', 'Verb', 'from «1» (as a negative particle) and «3539»; not to know (through lack of information or intelligence); by implication, to ignore (through disinclination):--(be) ignorant(-ly), not know, not understand, unknown.', '<OL TYPE=\"1\"><LI>to be ignorant, not to know<LI>not to understand, unknown<LI>to err or sin through mistake, to be wrong</OL>'),
(51, 'ajgnovhma', 'from (50)', 'agnoema', '1:115,18', 'ag-no\'-ay-mah', 'Noun Neuter', 'from «50»; a thing ignored, i.e. shortcoming:--error.', '<OL TYPE=\"1\"><LI>a sin committed through ignorance or thoughtlessness</OL>'),
(52, 'a[gnoia', 'from (50)', 'agnoia', '1:116,18', 'ag\'-noy-ah', 'Noun Feminine', 'from «50»; ignorance (properly, the quality):--ignorance.', '<OL TYPE=\"1\"><LI>lack of knowledge, ignorance<OL TYPE=\"a\"><LI>esp. of divine things<LI>of moral blindness</OL></OL>'),
(53, 'aJgnovß', 'from the same as (40)', 'hagnos', '1:122,19', 'hag-nos\'', 'Adjective', 'from the same as «40»; properly, clean, i.e. (figuratively) innocent, modest, perfect:--chaste, clean, pure.', '<OL TYPE=\"1\"><LI>exciting reverence, venerable, sacred<LI>pure<OL TYPE=\"a\"><LI>pure from carnality, chaste, modest<LI>pure from every fault, immaculate<LI>clean</OL></OL>'),
(54, 'aJgnovthß', 'from (53)', 'hagnotes', '1:124,19', 'hag-not\'-ace', 'Noun Feminine', 'from «53»; cleanness (the state), i.e. (figuratively) blamelessness:--pureness.', '<OL TYPE=\"1\"><LI>purity, chastity, uprightness of life</OL>'),
(55, 'aJgnw\'ß', 'from (53)', 'hagnos', 'None', 'hag-noce\'', 'Adverb', 'adverb from «53»; purely, i.e. honestly:--sincerely.', '<OL TYPE=\"1\"><LI>chaste, clean, pure, with sincerity</OL>'),
(56, 'ajgnwsiva', 'from (1) (as negative particle) and (1108)', 'agnosia', '1:116,18', 'ag-no-see\'-ah', 'Noun Feminine', 'from «1» (as negative particle) and «1108»; ignorance (properly, the state):--ignorance, not the knowledge.', '<OL TYPE=\"1\"><LI>not knowing, ignorance</OL>'),
(57, 'a[gnwstoß', 'from (1) (as negative particle) and (1110)', 'agnostos', '1:119,18', 'ag\'-noce-tos\'', 'Adjective', 'from «1» (as negative particle) and «1110»; unknown:--unknown.', '<OL TYPE=\"1\"><LI>unknown, forgotten</OL>Epimenides, a certain Cretan, came to Athens to stop a plague. Heoffered a sheep on alters the born no name of no god but to the\"unknown god\". At least one of the alters survived to Paul\'s day.'),
(58, 'ajgorav', 'from ageiro (to gather, probably akin to (1453))', 'agora', 'None', 'ag-or-ah\'', 'Noun Feminine', 'from ageiro (to gather; probably akin to «1453»); properly, the town-square (as a place of public resort); by implication, a market or thoroughfare:--market(-place), street.', '<OL TYPE=\"1\"><LI>any assembly, especially of the people<LI>the place of assembly<OL TYPE=\"a\"><LI>for public debating,<LI>for elections<LI>for trials<LI>for buying and selling<LI>for all kinds of business</OL><LI>market place, street</OL>'),
(59, 'ajgoravzw', 'from (58)', 'agorazo', '1:124,19', 'ag-or-ad\'-zo', 'Verb', 'from «58»; properly, to go to market, i.e. (by implication) to purchase; specially, to redeem:--buy, redeem.', '<OL TYPE=\"1\"><LI>to be in the market place, to attend it<LI>to do business there, buy or sell<LI>of idle people: to haunt the market place, lounge there</OL>'),
(60, 'ajgorai\'oß', 'from (58)', 'agoraios', 'None', 'ag-or-ah\'-yos', 'Adjective', 'from «58»; relating to the market-place, i.e. forensic (times); by implication, vulgar:--baser sort, low.', '<OL TYPE=\"1\"><LI>in, of or belonging to the market place<LI>frequenting the market place<OL TYPE=\"a\"><LI>hucksters, petty traffickers, retail dealers<LI>idlers, loungers, the common sort, low, mean vulgar</OL><LI>generally, proper to the assembly, suited to forensic speaking, business-like transactions</OL>'),
(61, 'a[gra', 'from (71)', 'agra', 'None', 'ag\'-rah', 'Noun Feminine', 'from «71»; (abstractly) a catching (of fish); also (concretely) a haul (of fish):--draught.', '<OL TYPE=\"1\"><LI>a catching, hunting<LI>that which is taken in hunting, the booty, prey<OL TYPE=\"a\"><LI>of birds or beasts, game<LI>of fish, a draught or haul</OL></OL>'),
(62, 'ajgravmmatoß', 'from (1) (as negative particle) and (1121)', 'agrammatos', 'None', 'ag-ram-mat-os', 'Adjective', 'from «1» (as negative particle) and «1121»; unlettered, i.e. illiterate:--unlearned.', '<OL TYPE=\"1\"><LI>illiterate, unlearned</OL>'),
(63, 'ajgraulevw', 'from (68) and (832) (in the sense of (833))', 'agrauleo', 'None', 'ag-row-leh\'-o', 'Verb', 'from «68» and «832» (in the sense of «833»); to camp out:--abide in the field.', '<OL TYPE=\"1\"><LI>to live in the fields, be under the open sky, even at night</OL>'),
(64, 'ajgreuvw', 'from (61)', 'agreuo', 'None', 'ag-rew\'-o', 'Verb', 'from «61»; to hunt, i.e. (figuratively) to entrap:--catch.', '<OL TYPE=\"1\"><LI>to hunt, to take by hunting, catch<LI>metaph. to hunt after, pursue eagerly</OL>'),
(65, 'ajgrievlaioß', 'from (66) and (1636)', 'agrielaios', 'None', 'ag-ree-el\'-ah-yos', 'Adjective', 'from «66» and «1636»; an oleaster:--olive tree (which is) wild.', '<OL TYPE=\"1\"><LI>of or belonging to the oleaster or wild olive<LI>the oleaster, wild olive tree</OL>'),
(66, 'a[grioß', 'from (68)', 'agrios', 'None', 'ag\'-ree-os', 'Adjective', 'from «68»; wild (as pertaining to the country), literally (natural) or figuratively (fierce):--wild, raging.', '<OL TYPE=\"1\"><LI>living or growing in the fields or woods<OL TYPE=\"a\"><LI>of animals, wild, savage<LI>of countries, wild, uncultivated, unreclaimed</OL><LI>of men and animals in a moral sense, wild savage, fierce<OL TYPE=\"a\"><LI>boorish, rude<LI>of any violent passion, vehement, furious</OL></OL>'),
(67, 'ÅAgrivppaß', 'apparently from (66) and (2462)', 'Agrippas', 'None', 'ag-rip\'-pas', 'Noun Masculine', 'apparently from «66» and «2462»; wild-horse tamer; Agrippas, one of the Herods:--Agrippa.', 'Agrippa = \"Hero like\"<OL TYPE=\"1\"><LI>Name of a ruling family in Israel at the time of Christ</OL>'),
(68, 'ajgrovß', 'from (71)', 'agros', 'None', 'ag-ros\'', 'Noun Masculine', 'from «71»; a field (as a drive for cattle); genitive case, the country; specially, a farm, i.e. hamlet:--country, farm, piece of ground, land.', '<OL TYPE=\"1\"><LI>land<OL TYPE=\"a\"><LI>the field, the country<LI>a piece of land, bit of tillage<LI>the farms, country seats, neighbouring hamlets</OL></OL>'),
