INSERT INTO `hebrew_lexicon` (`number`, `orig_word`, `word_orig`, `translit`, `tdnt`, `phonetic`, `part_of_speech`, `st_def`, `IPD_def`) VALUES
(1, 'ba', 'a root', '\'ab', 'TWOT - 4a', 'awb', 'Noun Masculine', 'a primitive word; father, in a literal and immediate, or figurative and remote application):--chief, (fore-)father(-less), ×  patrimony, principal. Compare names in \"Abi-\".', '<OL TYPE=\"1\"><LI>father of an individual<LI>of God as father of his people<LI>head or founder of a household, group, family, or clan<LI>ancestor<OL TYPE=\"a\"><LI>grandfather, forefathers -- of person<LI>of people</OL><LI>originator or patron of a class, profession, or art<LI>of producer, generator (fig.)<LI>of benevolence and protection (fig.)<LI>term of respect and honour<LI>ruler or chief (spec.)</OL>'),
(2, 'ba', 'corresponding to (01)', '\'ab', 'TWOT - 2553', 'ab', 'Noun Masculine', '(Aramaic) corresponding to «01»:--father.', '<OL TYPE=\"1\"><LI>father</OL>'),
(3, 'ba', 'from the same as (024)', '\'eb', 'TWOT - 1a', 'abe', 'Noun Masculine', 'from the same as «024»; a green plant:--greenness, fruit.', '<OL TYPE=\"1\"><LI>freshness, fresh green, green shoots, or greenery</OL>'),
(4, 'ba', 'corresponding to (03)', '\'eb', 'TWOT - 2554', 'abe', 'Noun Masculine', '(Aramaic) corresponding to «03»:--fruit.', '<OL TYPE=\"1\"><LI>fruit, fresh, young, greening</OL>'),
(5, 'atgba', 'of foreign origin', '\'Abagtha\'', 'None', 'ab-ag-thaw\'', 'Proper Name Masculine', 'of foreign origin; Abagtha, a eunuch of Xerxes:--Abagtha.', 'Abagtha = \"God-given\"<OL TYPE=\"1\"><LI>one of the seven eunuchs in the Persian court of Ahasuerus</OL>'),
(6, 'dba', 'a primitive root', '\'abad', 'TWOT - 2', 'aw-bad\'', 'Verb', 'a primitive root; properly, to wander away, i.e. lose oneself; by implication to perish (causative, destroy):--break, destroy(- uction), + not escape, fail, lose, (cause to, make) perish, spend, ×  and surely, take, be undone, ×  utterly, be void of, have no way to flee.', '<OL TYPE=\"1\"><LI>perish, vanish, go astray, be destroyed<OL TYPE=\"a\"><LI>(Qal)<OL TYPE=\"1\"><LI>perish, die, be exterminated<LI>perish, vanish (fig.)<LI>be lost, strayed</OL><LI>(Piel)<OL TYPE=\"1\"><LI>to destroy, kill, cause to perish, to give up (as lost), exterminate<LI>to blot out, do away with, cause to vanish, (fig.)<LI>cause to stray, lose</OL><LI>(Hiphil)<OL TYPE=\"1\"><LI>to destroy, put to death 1c</OL><LI>of divine judgment<OL TYPE=\"2\"><LI>object name of kings (fig.)</OL></OL></OL>'),
(7, 'dba', 'corresponding to (06)', '\'abad', 'TWOT - 2555', 'ab-ad\'', 'Verb', '(Aramaic) corresponding to «06»:--destroy, perish.', '<OL TYPE=\"1\"><LI>to perish, vanish<OL TYPE=\"a\"><LI>(P\'al) shall perish<LI>(Aphel) destroy<LI>(Hophal) be destroyed</OL></OL>'),
(8, 'dba', 'active of participle of (06)', '\'obed', 'TWOT - 2a', 'o-bade\'', 'Noun Masculine', 'active of participle of «06»; (concrete) wretched or (abstract) destructin:--perish.', '<OL TYPE=\"1\"><LI>destruction</OL>'),
(9, 'hdba', 'from (06)', '\'abedah', 'TWOT - 2b', 'ab-ay-daw\'', 'Noun Feminine', 'from «06»; concrete, something lost; abstract, destruction, i.e. Hades:--lost. Compare «010».', '<OL TYPE=\"1\"><LI>a lost thing, something lost</OL>'),
(10, 'hddba', 'the same as (09), incorrectly written for (11)', '\'abaddoh', 'TWOT - 2b', 'ab-ad-do\'', 'Noun Feminine', 'the same as «09», miswritten for «011»; a perishing:-- destruction.', '<OL TYPE=\"1\"><LI>a lost thing, something lost, a perishing</OL>'),
(11, '!wddba', 'intensive from (06)', '\'abaddown', 'TWOT - 2d', 'ab-ad-done\'', 'Proper Name Location', 'intensive from «06»; abstract, a perishing; concrete, Hades:--destruction.', '<OL TYPE=\"1\"><LI>place of destruction, destruction, ruin, Abaddon</OL>'),
(12, '!dba', 'from (06)', '\'abdan', 'TWOT - 2c', 'ab-dawn\'', 'Noun Masculine', 'from «06»; a perishing:--destruction.', '<OL TYPE=\"1\"><LI>destruction</OL>'),
(13, '!dba', 'from (06)', '\'obdan', 'TWOT - 2c', 'ob-dawn\'', 'Noun Masculine', 'from «06»; a perishing:--destruction.', '<OL TYPE=\"1\"><LI>destruction</OL>'),
(14, 'hba', 'a primitive root', '\'abah', 'TWOT - 3', 'aw-baw\'', 'Verb', 'a primitive root; to breathe after, i.e. (figuratively) to be acquiescent:--consent, rest content will, be willing.', '<OL TYPE=\"1\"><LI>to be willing, consent<OL TYPE=\"a\"><LI>(Qal)<OL TYPE=\"1\"><LI>to be willing<LI>to consent, yield to, accept<LI>to desire</OL></OL></OL>'),
(15, 'hba', 'from (014)', '\'abeh', 'TWOT - 3', 'aw-beh\'', 'Noun Feminine', 'from «014»; longing:--desire.', '<OL TYPE=\"1\"><LI>entreat, longing, desire</OL>'),
(16, 'hba', 'from (014) (in the sense of bending toward)', '\'ebeh', 'TWOT - 3c', 'ay-beh\'', 'Noun Masculine', 'from «014» (in the sense of bending toward); the papyrus:--swift.', '<OL TYPE=\"1\"><LI>reed, papyrus ++++ Probably ships made of reeds or papyrus were the clipper sailing ships of those days because of their speed.</OL>'),
(17, 'ywba', 'from (014) (in the sense of desiring)', '\'abowy', 'TWOT - 3d', 'ab-o\'ee', 'Interjection', 'from «014» (in the sense of desiring); want:--sorrow.', '<OL TYPE=\"1\"><LI>Oh!, Woe!, (exclamation of pain -- indicates desire or uneasiness)</OL>'),
(18, 'owba', 'from (075)', '\'ebuwc', 'TWOT - 10a', 'ay-booce\'', 'Noun Masculine', 'from «075»; a manger or stall:--crib.', '<OL TYPE=\"1\"><LI>crib, manger, feeding trough</OL>'),
(19, 'hxba', 'from an unused root (apparently meaning to turn)', '\'ibchah', 'TWOT - 786b', 'ib-khaw\'', 'Noun Feminine', 'from an unused root (apparently meaning to turn); brandishing of a sword:--point.', '<OL TYPE=\"1\"><LI>slaughter, flesh, meat, slaughtered meat</OL>'),
(20, 'xyttba', 'of uncertain derivation', '\'abattiyach', 'TWOT - 234a', 'ab-at-tee\'-akh', 'Noun Masculine', 'of uncertain derivation; a melon (only plural):--melon.', '<OL TYPE=\"1\"><LI>watermelon, Egyptian fruit</OL>'),
(21, 'yba', 'from (01)', '\'Abiy', 'None', 'ab-ee\'', 'Proper Name Feminine', 'from «01»; fatherly; Abi, Hezekiah\'s mother:--Abi.', 'Abi = \"my father\"<OL TYPE=\"1\"><LI>mother of Hezekiah (cf<LI></OL>'),
(22, 'layba', 'from (01) and (0410)', '\'Abiy\'el', 'None', 'ab-ee-ale\'', 'Proper Name Masculine', 'from «01» and «0410»; father (i.e. possessor) of God; Abiel, the name of two Israelites:--Abiel.', 'Abiel = \"El (God) is (my) father\"<OL TYPE=\"1\"><LI>Saul\'s grandfather</OL>'),
(23, '@oayba', 'from (01) and (0622)', '\'Abiy\'acaph', 'None', 'ab-ee-aw-sawf\'', 'Proper Name Masculine', 'from «01» and «0622»; father of gathering (i.e. gatherer); Abiasaph, an Israelite:--Abiasaph.', 'Abiasaph = \"my father has gathered\"<OL TYPE=\"1\"><LI>son (descendant) of Korah</OL>'),
(24, 'byba', 'from an unused root (meaning to be tender)', '\'abiyb', 'TWOT - 1b', 'aw-beeb\'', 'Noun Masculine', 'from an unused root (meaning to be tender); green, i.e. a young ear of grain; hence, the name of the month Abib or Nisan:--Abib, ear, green ears of corn (not maize).', '<OL TYPE=\"1\"><LI>fresh, young barley ears, barley<LI>month of ear-forming, of greening of crop, of growing green Abib, month of exodus and passover (March or April)</OL>'),
(25, '!w[bg yba', 'from (01) and (01391)', '\'Abiy', 'None', '', 'Proper Name Masculine', 'from «01» and «01391»; father (i.e. founder) of Gibon; Abi-Gibon, perhaps an Israelite:--father of Gibeon.', 'Abi Gibon = \"father of Gibeon\"<OL TYPE=\"1\"><LI>site of great Bamah, location of the tabernacle of the Lord in the high place</OL>'),
(26, 'lygyba', 'from (01) and (01524)', '\'Abiygayil', 'None', 'ab-ee-gah\'-yil', 'Proper Name Feminine', 'or shorter Abiygal {ab-ee-gal\'}; from «01» and «01524»; father (i.e. source) of joy; Abigail or Abigal, the name of two Israelitesses:--Abigal.', 'Abigail = \"my father is joy\"<OL TYPE=\"1\"><LI>wife of Nabal, then of David<LI>sister of David</OL>'),
(27, '!dyba', 'from (01) and (01777)', '\'Abiydan', 'None', 'ab-ee-dawn\'', 'Proper Name Masculine', 'from «01» and «01777»; father of judgment (i.e. judge); Abidan, an Israelite:--Abidan.', 'Abidan = \"my father is judge\"<OL TYPE=\"1\"><LI>a prince (ruler) of Benjamin</OL>'),
(28, '[dyba', 'from (01) and (03045)', '\'Abiyda`', 'None', 'ab-ee-daw\'', 'Proper Name Masculine', 'from «01» and «03045»; father of knowledge (i.e. knowing); Abida, a son of Abraham by Keturah:--Abida, Abidah.', 'Abida or Abidah = \"my father knows\"<OL TYPE=\"1\"><LI>fourth son of Midian and grandson of Abraham by his wife Keturah (after Sarah died)</OL>'),
(29, 'hyba', 'from (01) and (03050)', '\'Abiyah', 'None', 'ab-ee-yaw\'', 'Proper Name Masculine', 'or prolonged Abiyahuw {ab-ee-yaw\'-hoo}; from «01» and «03050»; father (i.e. worshipper) of Jah; Abijah, the name of several Israelite men and two Israelitesses:--Abiah, Abijah.', 'Abia or Abiah or Abijah = \"Jehovah is (my) father\"<OL TYPE=\"1\"><LI>king of Judah, son and successor of Rehoboam<LI>second son of Samuel<LI>son of Jeroboam the first, king of Israel<LI>son of Becher, a Benjamite<LI>head of a priestly house (one of the 24 Levite groups)<LI>head of a priestly house (after the exile)<LI>wife of Hezron<LI>mother of Hezekiah (cf<LI></OL>'),
(30, 'awhyba', 'from (01) and (01931)', '\'Abiyhuw\'', 'None', 'ab-ee-hoo\'', 'Proper Name Masculine', 'from «01» and «01931»; father (i.e. worshipper) of Him (i.e. God); Abihu, a son of Aaron:--Abihu.', 'Abihu = \"he is (my) father\"<OL TYPE=\"1\"><LI>a son of Aaron destroyed for sacrificing strange fire to God</OL>'),
(31, 'dwhyba', 'from (01) and (01935)', '\'Abiyhuwd', 'None', 'ab-ee-hood\'', 'Proper Name Masculine', 'from «01» and «01935»; father (i.e. possessor) of renown; Abihud, the name of two Israelites:--Abihud.', 'Ahihud = \"my father is majesty\"<OL TYPE=\"1\"><LI>son of Bela, a Benjamite</OL>'),
(32, 'lyhyba', 'from (01) and (02428)', '\'Abiyhayil', 'None', 'ab-ee-hah\'-yil', 'Proper Name Masculine', 'or (more correctly) hAbiychayil {ab-ee- khah\'-yil}; from 1 and «02428»; father (i.e. possessor) of might; Abihail or Abichail, the name of three Israelites and two Israelitesses:--Abihail.', 'Abihail = \"my father is might\"<OL TYPE=\"1\"><LI>a Levite in Moses\'s time<LI>a Gadite<LI>father of Esther<LI>wife of Abishur<LI>wife of Rehoboam</OL>'),
(33, 'yrz[h yba', 'from (044) with the article inserted', '\'Abiy', 'None', '', 'Proper Name Masculine', 'from «044» with the article inserted; father of the Ezrite; an Abiezrite or descendant of Abiezer; -- Abiezrite.', 'Abiezrite = \"my father is help\"<OL TYPE=\"1\"><LI>one of the family of Abiezer, a descendant of Joseph\'s son, Manasseh</OL>'),
(34, '!wyba', 'from (014), in the sense of want (especially in feeling)', '\'ebyown', 'TWOT - 3a', 'eb-yone\'', '', 'from «014», in the sense of want (especially in feeling); destitute:--beggar, needy, poor (man).', '<OL TYPE=\"1\"><LI>in want, needy, chiefly poor, needy person<LI>subject to oppression and abuse<LI>needing help, deliverance from trouble, especially as delivered by God<LI>general reference to lowest class</OL>'),
(35, 'hnwyba', 'from (014)', '\'abiyownah', 'TWOT - 3b', 'ab-ee-yo-naw\'', 'Noun Feminine', 'from «014»; provocative of desire; the caper berry (from its stimulative taste):--desire.', '<OL TYPE=\"1\"><LI>caper-berry - of stimulating desire</OL>'),
(36, 'bwtyba', 'from (01) and (02898)', '\'Abiytuwb', 'None', 'ab-ee-toob\'', 'Proper Name Masculine', 'from «01» and «02898»; father of goodness (i.e. good); Abitub, an Israelite:--Abitub.', 'Abitub = \"my father is good(ness)\"<OL TYPE=\"1\"><LI>son of Shaharaim, a Benjamite</OL>'),
(37, 'ltyba', 'from (01) and (02919)', '\'Abiytal', 'None', 'ab-ee-tal\'', 'Proper Name Feminine', 'from «01» and «02919»; father of dew (i.e. fresh); Abital, a wife of King David:--Abital.', 'Abital = \"my father is (the) dew\"<OL TYPE=\"1\"><LI>a wife of David</OL>'),
(38, '~yba', 'from (01) and (03220)', '\'Abiyam', 'None', 'ab-ee-yawm\'', 'Proper Name Masculine', 'from «01» and «03220»; father of (the) sea (i.e. seaman); Abijam (or Abijah), a king of Judah:--Abijam.', 'Abijam = \"my father is the sea\" or \"Yah(u) is (my) father\"<OL TYPE=\"1\"><LI>king of Judah, son and successor of Rehoboam</OL>'),
(39, 'lamyba', 'from (01) and an elsewhere unused (probably foreign) word', '\'Abiyma\'el', 'pr n m', 'ab-ee-maw-ale\'', '', 'from «01» and an elsewhere unused (probably foreign) word; father of Mael (apparently some Arab tribe); Abimael, a son of Joktan:--Abimael.', 'Abimael = \"my father is El (God)\"<OL TYPE=\"1\"><LI>son of Joktan, descendant of Shem</OL>'),
(40, '$lmyba', 'from (01) and (04428)', '\'Abiymelek', 'None', 'ab-ee-mel\'-ek', 'Proper Name Masculine', 'from «01» and «04428»; father of (the) king; Abimelek, the name of two Philistine kings and of two Israelites:--Abimelech.', 'Abimelech = \"Melek is father\" or \"my father is king\"<OL TYPE=\"1\"><LI>king of Gerar in Abraham\'s time<LI>king of Gath in David\'s time; maybe title of Philistine kings<LI>son of Gideon by a concubine<LI>priest, son of Abiathar</OL>'),
(41, 'bdnyba', 'from (01) and (05068)', '\'Abiynadab', 'None', 'ab-ee-naw-dawb\'', 'Proper Name Masculine', 'from «01» and «05068»; father of generosity (i.e. liberal); Abinadab, the name of four Israelites:--Abinadab.', 'Abinadab = \"my father is noble\" or \"my father is willing\"<OL TYPE=\"1\"><LI>a man of Gibeah who sheltered the ark<LI>second son of Jesse, David\'s older brother<LI>a son of Saul, slain with him by the Philistines</OL>'),
(42, '~[nyba', 'from (01) and (05278)', '\'Abiyno`am', 'None', 'ab-ee-no\'-am', 'Proper Name Masculine', 'from «01» and «05278»; father of pleasantness (i.e. gracious); Abinoam, an Israelite:--Abinoam.', 'Abinoam = \"my father is delight\"<OL TYPE=\"1\"><LI>father of Barak, descendant of Naphtali</OL>'),
(43, '@oyba', 'contracted from (023)', '\'Ebyacaph', 'None', 'eb-yaw-sawf\'', 'Proper Name Masculine', 'contracted from «023»; Ebjasaph, an Israelite:--Ebiasaph.', 'Ebiasaph = \"my father has gathered\"<OL TYPE=\"1\"><LI>a son or descendant of Korah</OL>'),
(44, 'rz[yba', 'from (01) and (05829)', '\'Abiy`ezer', 'None', 'ab-ee-ay\'-zer', 'Proper Name Masculine', 'from «01» and «05829»; father of help (i.e. helpful); Abiezer, the name of two Israelites:--Abiezer.', 'Abiezer = \"my father is help\"<OL TYPE=\"1\"><LI>a Manassite, called \"son\" of Gilead, also son of Gilead\'s sister<LI>a Benjamite, warrior of David</OL>'),
(45, '!wbl[yba', 'from (01) and an unused root of uncertain derivation', '\'Abiy-`albown', 'None', 'ab-ee-al-bone\'', 'Proper Name Masculine', 'from «01» and and an unused root of uncertain. derivation; probably, father of strength (i.e. valiant); Abialbon, an Israelite:--Abialbon.', 'Abialbon = \"El (God) is my father\"<OL TYPE=\"1\"><LI>one of David\'s mighty men (hero)</OL>'),
(46, 'ryba', 'from (082)', '\'abiyr', 'TWOT - 13c', 'aw-beer\'', 'Adjective Masculine', 'from «082»; mighty (spoken of God):--mighty (one).', '<OL TYPE=\"1\"><LI>strong, mighty - used only to describe God<LI>the Strong - old name for God (poetic)</OL>'),
(47, 'rybba', 'for (046)', '\'abbiyr', 'TWOT - 13d', 'ab-beer\'', 'Adjective Masculine', 'for «046»; --angel, bull, chiefest, mighty (one), stout(-hearted), strong (one), valiant.', '<OL TYPE=\"1\"><LI>mighty, valiant<OL TYPE=\"a\"><LI>of men<LI>of angels<LI>of animals<LI>(metaph)<OL TYPE=\"1\"><LI>of enemies<LI>of princes<LI>of sacrificial objects</OL><LI>obstinate (fig.)</OL></OL>'),
(48, '~ryba', 'from (01) and (07311)', '\'Abiyram', 'None', 'ab-ee-rawm\'', 'Proper Name Masculine', 'from «01» and «07311»; father of height (i.e. lofty); Abiram, the name of two Israelites:--Abiram.', 'Abiram = \"my father is exalted\" or \"(the) Exalted One is (my) father\"<OL TYPE=\"1\"><LI>a Reubenite, son of Eliab in exodus<LI>son of Hiel the Bethelite who worked to rebuild Jericho</OL>'),
(49, 'gXyba', 'from (01) and (07686)', '\'Abiyshag', 'None', 'ab-ee-shag\'', 'Proper Name Feminine', 'from «01» and «07686»; father of error (i.e. blundering); Abishag, a concubine of David:--Abishag.', 'Abishag = \"my father is a wanderer\"<OL TYPE=\"1\"><LI>David\'s beautiful young nurse</OL>'),
(50, 'wXyba', 'from (01) and (07771)', '\'Abiyshuwae', 'None', 'ab-ee-shoo\'-ah', 'Proper Name Masculine', 'from «01» and «07771»; father of plenty (i.e. prosperous); Abishua, the name of two Israelites:--Abishua.', 'Abishua = \"my father is rescue (safety), or is opulence\"<OL TYPE=\"1\"><LI>son of Phinehas, grandson of Aaron</OL>'),
(51, 'rwXyba', 'from (01) and (07791)', '\'Abiyshuwr', 'None', 'ab-ee-shoor\'', 'Proper Name Masculine', 'from «01» and «07791»; father of (the) wall (i.e. perhaps mason); Abishur, an Israelite:--Abishur.', 'Abishur = \"my father is a wall\"<OL TYPE=\"1\"><LI>son of Shammai, descendant of Judah</OL>'),
(52, 'yXyba', 'from (01) and (07862)', '\'Abiyshay', 'None', 'ab-ee-shah\'ee', 'Proper Name Masculine', 'or (shorter) Abshay {ab-shah\'ee}; from «01» and «07862»; father of a gift (i.e. probably generous); Abishai, an Israelite:--Abishai.', 'Abishai = \"my father is Jesse\" or \"my father is a gift\"<OL TYPE=\"1\"><LI>grandson of Jesse, nephew of David via his sister Zeruiah, brother of Joab</OL>'),
(53, '~wlXyba', 'from (01) and (07965)', '\'Abiyshalowm', 'None', 'ab-ee-shaw-lome\'', 'Proper Name Masculine', 'or (shortened) bAbshalowm {ab-shaw- lome\'}; from «01» and «07965»; father of peace (i.e. friendly); Abshalom, a son of David; also (the fuller form) a later Israelite:--Abishalom, Absalom.', 'Absalom or Abishalom = \"my father is peace\"<OL TYPE=\"1\"><LI>father-in-law of Rehoboam<LI>third son of David, killer of first-born son Amnon, also leader of revolt against his father - David</OL>'),
(54, 'rtyba', 'contracted from (01) and (03498)', '\'Ebyathar', 'None', 'ab-yaw-thawr\'', 'Proper Name Masculine', 'contracted from «01» and «03498»; father of abundance (i.e. liberal); Ebjathar, an Israelite:--Abiathar.', 'Abiathar = \"my father is great\"<OL TYPE=\"1\"><LI>priest, son of Ahitub (Ahimelech), faithful to David, but later rebelled with Adonijah</OL>'),
(55, '$ba', 'a primitive root', '\'abak', 'TWOT - 5', 'aw-bak\'', 'Verb', 'a primitive root; probably to coil upward:--mount up.', '<OL TYPE=\"1\"><LI>to roll, turn<OL TYPE=\"a\"><LI>(Hithpael) to roll, roll up, billow</OL></OL>'),
(56, 'lba', 'a primitive root', '\'abal', 'TWOT - 6', 'aw-bal\'', 'Verb', 'a primitive root; to bewail:--lament, mourn.', '<OL TYPE=\"1\"><LI>to mourn, lament<OL TYPE=\"a\"><LI>(Qal) to mourn, lament<OL TYPE=\"1\"><LI>of humans<LI>of inanimate objects (fig.) 1a</OL><LI>of gates 1a<LI>of land<LI>(Hiphil)<OL TYPE=\"1\"><LI>to mourn, cause to mourn (fig.)</OL><LI>(Hithpael)<OL TYPE=\"1\"><LI>to mourn<LI>play the mourner</OL></OL></OL>'),
(57, 'lba', 'from (056)', '\'abel', 'TWOT - 6b', 'aw-bale\'', 'Adjective', 'from «056»; lamenting:--mourn(-er, -ing).', '<OL TYPE=\"1\"><LI>mourning<OL TYPE=\"a\"><LI>for the dead<LI>because of calamity<LI>of rites of mourning</OL><LI>mourner (subst.)<OL TYPE=\"a\"><LI>for the dead<LI>for calamity</OL></OL>'),
(58, 'lba', 'from an unused root (meaning to be grassy)', '\'abel', 'TWOT - 7a', 'aw-bale\'', 'Noun Feminine', 'from an unused root (meaning to be grassy); a meadow:--plain. Compare also the proper names beginning with Abel-.', '<OL TYPE=\"1\"><LI>meadow (from verb - to grow green, to withstand)</OL>'),
(59, 'lba', 'from (058)', '\'Abel', 'None', 'aw-bale\'', 'Proper Name Location', 'from «058»; a meadow; Abel, the name of two places in Palestine.:--Abel.', '<OL TYPE=\"1\"><LI>city in northern Israel near Bethmaachah<LI>the place where the ark rested in the field of Joshua at Bethshemesh</OL>'),
(60, 'lba', 'from (056)', '\'ebel', 'TWOT - 6a', 'ay\'-bel', 'Noun Masculine', 'from «056»; lamentation:--mourning.', '<OL TYPE=\"1\"><LI>mourning<OL TYPE=\"a\"><LI>for the dead<LI>for rites of mourning (metaph)<LI>mourning garb<LI>period of mourning</OL></OL>'),
(61, 'lba', 'apparently from (056) through the idea of negation', '\'abal', 'TWOT - 8', 'ab-awl\'', 'Adverb', 'apparently from «056» through the idea of negation; nay, i.e. truly or yet:--but, indeed, nevertheless, verily.', '<OL TYPE=\"1\"><LI>truly, verily, surely<LI>but, however, howbeit<LI>contrariwise, nay rather (neg.)</OL>'),
(62, 'hk[mtyb lba', 'from (058) and (01004) and (04601)', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and «01004» and «04601»; meadow of Beth-Maakah; Abel of Beth-maakah, a place in Palestine:--Abel-beth-maachah, Abel of Beth-maachah,', 'Abel Beth Maachah = \"meadow of the house of Maachah\"<OL TYPE=\"1\"><LI>city in northern Israel near Beth Maachah</OL>'),
(63, '~yttXXh lba', 'from (058) and the plural of (07848), with the article inserted', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and the plural of «07848», with the article inserted; meadow of the acacias; Abel hash- Shittim, a place in Palestine:--Abel-shittim.', 'Abel Shittim = \"meadow of acacias\"<OL TYPE=\"1\"><LI>place in lowlands of Moab</OL>'),
(64, '~ymrk lba', 'from (058) and the plural of (03754)', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and the plural of «03754»; meadow of vineyards; Abel-Keramim, a place in Palestine:--plain of the vineyards.', 'Abel Keramim = \"meadow of the vineyards\"<OL TYPE=\"1\"><LI>a place in Ammon</OL>'),
(65, 'hlwxm lba', 'from (058) and (04246)', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and «04246»; meadow of dancing; Abel-Mecholah, a place in Palestine:--Abel-meholah.', 'Abel Meholah = \"meadow of dancing\"<OL TYPE=\"1\"><LI>a city of Issachar, birthplace of Elisha</OL>'),
(66, '~ym lba', 'from (058) and (04325)', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and «04325»; meadow of water; Abel-Majim, a place in Palestine:--Abel-maim.', 'Abel Maim = \"meadow of waters\"<OL TYPE=\"1\"><LI>a city in northern Israel (perhaps Abel Beth Maachah)</OL>'),
(67, '~yrcm lba', 'from (058) and (04714)', '\'Abel', 'None', '', 'Proper Name Location', 'from «058» and «04714»; meadow of Egypt; Abel-Mitsrajim, a place in Palestine:--Abel-mizraim.', 'Abel Mizraim = \"meadow of Egypt\"<OL TYPE=\"1\"><LI>a place east of the Jordan (perhaps \"as fertile as Egypt\")</OL>'),
(68, '!ba', 'from the root of (01129) through the meaning to build', '\'eben', 'TWOT - 9', 'eh\'-ben', 'Noun Feminine', 'from the root of «01129» through the meaning to build; a stone:--+ carbuncle, + mason, + plummet, (chalk-, hail-, head-, sling-)stone(-ny), (divers) weight(-s).', '<OL TYPE=\"1\"><LI>stone (large or small)<OL TYPE=\"a\"><LI>common stone (in natural state)<LI>stone, as material<OL TYPE=\"1\"><LI>of tablets<LI>marble, hewn stones</OL><LI>precious stones, stones of fire<LI>stones containing metal (ore), tool for work or weapon<LI>weight<LI>plummet (stones of destruction) also made of metal<LI>stonelike objects, eg hailstones, stony heart, ice<LI>sacred object, as memorial Samuel set up to mark where God helped Israel to defeat the Philistines<LI>(simile)<OL TYPE=\"1\"><LI>sinking in water, motionlessness<LI>strength, firmness, solidity<LI>commonness</OL><LI>(metaph)<OL TYPE=\"1\"><LI>petrified with terror<LI>perverse, hard heart</OL></OL></OL>'),
(69, '!ba', 'corresponding to (068)', '\'eben', 'TWOT - 2556', 'eh\'-ben', 'Noun Feminine', '(Aramaic) corresponding to «068»:--stone.', '<OL TYPE=\"1\"><LI>stone<OL TYPE=\"a\"><LI>a (the) stone<LI>stone, material of idols and buildings</OL></OL>'),
(70, '!ba', 'from the same as (068)', '\'oben', 'TWOT - 9a', 'o\'ben', 'Noun Masculine', 'from the same as «068»; a pair of stones (only dual); a potter\'s wheel or a midwife\'s stool (consisting alike of two horizontal disks with a support between):--wheel, stool.', '<OL TYPE=\"1\"><LI>wheel, disc<OL TYPE=\"a\"><LI>potter\'s wheel<LI>bearing-stool, midwife\'s stool</OL></OL>'),
(71, 'hnba', 'perhaps f of (068)', '\'Abanah', 'None', 'ab-aw-naw\'', 'Noun', 'perhaps feminine of «068»; stony; Abanah, a river near Damascus:--Abana. Compare «0549».', '<OL TYPE=\"1\"><LI>river Abana, Syrian river flowing through Damascus</OL>'),
(72, 'rz[h !ba', 'from (068) and (05828) with the article inserted', '\'Eben', 'None', '', 'Noun', 'from «068» and «05828» with the article inserted; stone of the help; Eben-ha-Ezer, a place in Palestine:--Ebenezer.', 'Ebenezer = \"stone of help\"<OL TYPE=\"1\"><LI>memorial stone erected by Samuel to mark where God helped Israel to defeat the Philistines - north of Jerusalem</OL>'),
(73, 'tnba', 'of uncertain derivation', '\'abnet', 'TWOT - 256a', 'ab-nate\'', 'Noun Masculine', 'of uncertain derivation; a belt:--girdle.', '<OL TYPE=\"1\"><LI>girdle, sash, waistband<OL TYPE=\"a\"><LI>of high priest<LI>of other priests<LI>of high official</OL></OL>'),
(74, 'rnba', 'from (01) and (05216)', '\'Abner', 'None', 'ab-nare\'', 'Proper Name Masculine', 'or (fully) nAbiyner {ab-ee-nare\'}; from «01» and «05216»; father of light (i.e. enlightening); Abner, an Israelite:--Abner.', 'Abner = \"my father is a lamp\"<OL TYPE=\"1\"><LI>Saul\'s cousin and army captain, treacherously slain by Joab</OL>'),
