(85, '~hrba', 'contracted from (01) and an unused root (probably meaning to be populous)', '\'Abraham', 'TWOT - 4b', 'ab-raw-hawm\'', 'Proper Name Masculine', 'contracted from «01» and an unused root (probably meaning to be populous); father of a multitude; Abraham, the later name of Abram:--Abraham.', 'Abraham = \"father of a multitude\" or \"chief of multitude\"<OL TYPE=\"1\"><LI>friend of God and founder of Hebrew nation via God\'s elective covenant</OL>'),
(86, '$rba', 'probably an Egyptian word meaning kneel', '\'abrek', 'TWOT - 14', 'ab-rake\'', '', 'probably an Egyptian word meaning kneel:--bow the knee.', '<OL TYPE=\"1\"><LI>(meaning dubious) - a shout made to announce Joseph\'s chariot<OL TYPE=\"a\"><LI>command<LI>bow</OL></OL>'),
(87, '~rba', 'contracted from (048)', '\'Abram', 'None', 'ab-rawm\'', 'Proper Name Masculine', 'contracted from «048»; high father; Abram, the original name of Abraham:--Abram.', 'Abram = \"exalted father\"<OL TYPE=\"1\"><LI>original name of Abraham</OL>'),
