(5461, '!go', 'from an unused root meaning to superintend', 'cagan', 'TWOT - 1461', 'saw-gawn\'', 'Noun Masculine', 'from an unused root meaning to superintend; a prefect of a province:--prince, ruler.', '<OL TYPE=\"1\"><LI>ruler, prefect, governor, a subordinate ruler<OL TYPE=\"a\"><LI>prefects (of Assyria and Babylon)<LI>petty rulers or officials</OL></OL>'),
(5462, 'rgo', 'a primitive root', 'cagar', 'TWOT - 1462', 'saw-gar\'', 'Verb', 'a primitive root; to shut up; figuratively, to surrender:--close up, deliver (up), give over (up), inclose, × pure, repair, shut (in, self, out, up, up together), stop, ×  straitly.', '<OL TYPE=\"1\"><LI>to shut, close<OL TYPE=\"a\"><LI>(Qal)<OL TYPE=\"1\"><LI>to shut<LI>to close, close up<LI>closed up, closely joined, shut up</OL><LI>(Niphal)<OL TYPE=\"1\"><LI>to be shut up<LI>to be shut or closed</OL><LI>(Piel) to shut up, deliver up<LI>(Pual) to be shut up<LI>(Hiphil)<OL TYPE=\"1\"><LI>to deliver up<LI>to shut up, imprison</OL></OL></OL>'),
(5463, 'rgo', 'corresponding to (05462)', 'cgar', 'TWOT - 2886', 'seg-ar\'', 'Verb', '(Aramaic) corresponding to «05462»:--shut up.', '<OL TYPE=\"1\"><LI>(P\'al) to shut<LI>(TWOT) shirt</OL>'),
